mapper.update:
update t_serial_number_record
set last_number = #{lastNumber},
count = count + #{count}
where
serial_number_id = #{serialNumberId}
and
record_date = #{recordDate}

mapper.select:
select serial_number_record_id
from t_serial_number_record
where
serial_number_id = #{serialNumberId}
and
record_date = #{recordDate}
