mapper.select:
SELECT
e.*,
d.name AS departmentName
FROM t_employee e
LEFT JOIN t_department d ON d.id = e.department_id


        AND (
           INSTR(e.actual_name,#{queryDTO.keyword})
        OR INSTR(e.phone,#{queryDTO.keyword})
        )


        AND e.department_id = #{queryDTO.departmentId}


        AND e.disabled_flag = #{queryDTO.disabledFlag}


        AND e.deleted_flag = #{queryDTO.deletedFlag}


        AND e.id IN

            #{item}




    ORDER BY e.id DESC

mapper.update:
UPDATE t_employee
SET disabled_flag = #{disabledFlag}
WHERE id in

    #{item}

UPDATE t_employee
SET disabled_flag = #{disabledFlag}
WHERE id = #{id}

mapper.select:
SELECT *
FROM t_employee
WHERE login_name = #{loginName}
AND login_pwd = #{loginPwd}
AND deleted_flag = #{deletedFlag}
LIMIT 1

SELECT count(1) FROM t_employee
WHERE
department_id = #{departmentId}

    AND deleted_flag = #{deletedFlag}

SELECT * FROM t_employee
WHERE
disabled_flag = #{disabledFlag}
AND deleted_flag = #{deletedFlag}
AND department_id = #{departmentId}
ORDER BY create_time DESC

SELECT id
FROM t_employee
WHERE
disabled_flag = #{disabledFlag}
AND deleted_flag = #{deletedFlag}
AND department_id IN

    #{item}

ORDER BY create_time DESC

SELECT id
FROM t_employee
WHERE
disabled_flag = #{disabledFlag}
AND deleted_flag = #{deletedFlag}

SELECT id
FROM t_employee
WHERE
disabled_flag = #{disabledFlag}
and deleted_flag = #{deletedFlag}
and department_id = #{departmentId}
ORDER BY create_time DESC

SELECT * FROM t_employee where id IN
#{item}
ORDER BY create_time DESC

mapper.update:
UPDATE t_employee
SET login_pwd = #{password}
WHERE id = #{employeeId}
