mapper.select:
SELECT e.*, d.name AS departmentName FROM t_employee e LEFT JOIN t_department d ON d.department_id = e.department_id
AND ( INSTR(e.actual_name,#{queryForm.keyword}) OR INSTR(e.phone,#{queryForm.keyword}) OR INSTR(e.login_name,#{queryForm.keyword}) )
AND e.department_id = #{queryForm.departmentId}
AND e.disabled_flag = #{queryForm.disabledFlag}

mapper.update:
UPDATE t_employee
SET disabled_flag = #{disabledFlag}
WHERE employee_id = #{employeeId}

mapper.select:
SELECT *
FROM t_employee

    login_name = #{loginName}

        AND disabled_flag = #{disabledFlag}

SELECT *
FROM t_employee

    actual_name = #{actualName}

        AND disabled_flag = #{disabledFlag}

SELECT *
FROM t_employee

    phone = #{phone}

        AND disabled_flag = #{disabledFlag}

SELECT count(1) FROM t_employee
WHERE
department_id = #{departmentId}

    AND deleted_flag = #{deletedFlag}

SELECT *
FROM t_employee

    department_id = #{departmentId}

        AND disabled_flag = #{disabledFlag}


ORDER BY create_time DESC

