mapper.select:
SELECT er.role_id
FROM t_role_employee er
WHERE er.employee_id = #{employeeId}

SELECT e.id, e.login_name, e.login_pwd, e.actual_name, e.phone, e.department_id, e.disabled_flag, e.remark, e.update_time, e.create_time FROM t_role_employee er left join t_employee e on er.employee_id = e.id left join t_role r on er.role_id = r.id
AND ( INSTR(e.actual_name,#{queryForm.keywords}) OR INSTR(e.phone,#{queryForm.keywords}) OR INSTR(e.login_name,#{queryForm.keywords}) )
AND er.role_id = #{queryForm.roleId}

mapper.delete:
DELETE
FROM t_role_employee
WHERE role_id = #{roleId}
  and employee_id = #{employeeId}

DELETE FROM t_role_employee
WHERE role_id = #{roleId} and employee_id in

    #{item}
